mapper.update:
UPDATE tbl_post_attachments
SET delete_yn = 1
WHERE post_attachment_id = #{id}
  AND post_id = #{postId};

mapper.delete:
DELETE
FROM tbl_post_attachments
WHERE post_attachment_id = #{id}
  AND delete_yn = 1;

mapper.update:
