mapper.update:
UPDATE tbl_comments


        content = #{content},
        last_modified_date = CURRENT_TIMESTAMP


WHERE comment_id = #{id}
AND member_id = #{writerId}
AND delete_yn = 0;

UPDATE tbl_comments
SET delete_yn = 1
WHERE comment_id = #{id}
  AND member_id = #{writerId}
  AND delete_yn = 0;

mapper.delete:
DELETE
FROM tbl_comments
WHERE comment_id = #{id}
  AND delete_yn = 1;

